import { useState } from "react";

// const initialItems = [
//     { id: 1, description: "Passports", quantity: 2, packed: false },
//     { id: 2, description: "Socks", quantity: 12, packed: true },
//     { id: 3, description: "Charger", quantity: 1, packed: false },
// ];

function App() {
    const [items, setItems] = useState([]);
    function handleAddItems(item) {
        setItems((items) => [...items, item]);
    }
    function handleDeleteItems(id) {
        setItems((items) => item.filter((item) => item.id !== id));
    }
    return (
        <div className="app">
            <Logo />
            <Form onAddItems={handleAddItems} />
            <PackingList items={items} />
            <Stats />
        </div>
    );
}
function Logo() {
    return <h1>üì¶ Far Away üå¥</h1>;
}
function Form({ onAddItems }) {
    const [description, setDescription] = useState("");
    const [quantity, setQuantity] = useState(1);

    function handleSubmit(event) {
        // prevent reload
        event.preventDefault();

        if (!description) return;
        const newItem = {
            description,
            quantity,
            packed: false,
            id: Date.now(),
        };
        onAddItems(newItem);
        setDescription("");
        setQuantity(1);
    }
    return (
        // listen for submit form
        // enter and click button can recognize
        <form className="add-form" onSubmit={handleSubmit}>
            <h3>What do you need for your üòé trip?</h3>
            <select
                value={quantity}
                onChange={(e) => setQuantity(Number(e.target.value))}
            >
                {/* Create an array of 20 and put it in option */}
                {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
                    <option value={num} key={num}>
                        {num}
                    </option>
                ))}
            </select>
            <input
                type="text"
                placeholder="Item ... "
                value={description}
                onChange={(event) => setDescription(event.target.value)}
            />
            <button>Add</button>
        </form>
    );
}
function PackingList({ items }) {
    return (
        <div className="list">
            <ul>
                {items.map((item) => (
                    <Item item={item} key={item.id} />
                ))}
            </ul>
        </div>
    );
}
function Item({ item }) {
    return (
        <li>
            <span style={item.packed ? { textDecoration: "line-through" } : {}}>
                {item.quantity} {item.description}
            </span>
            <button>‚ùå</button>
        </li>
    );
}
function Stats() {
    return (
        <footer className="stats">
            <em>
                üòè You have X items on your list, and you already packed X (X%)
            </em>
        </footer>
    );
}

export default App;
